package java250721;

public class Test04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int sum1 = 0;
		for (int i=0;i<=100;i=i+2) {
			sum1=sum1+i; //홀수	
			System.out.println ("홀수" + sum1);
		}
		int sum2 = 0;
		for (int i=0;i<=100;i=i+1) {
			sum2=sum2+i; //짝수	
			System.out.println ("짝수" + sum2);
			
		}
	
			
	}

}
